/* CUSTOMIZE THE CAROUSEL
    -------------------------------------------------- */

    /* Carousel base class */
    .carousel {
        max-width: 1440px;
        height: 500px;
        margin: 5px auto;
        margin-bottom: 20px;
    }

    .carousel .ccc {
        position: absolute;
        top: 0;
        left: 0;
        z-index: 9;
    }

    .carousel-control {
        height: 80px;
        margin-top: 0;
        font-size: 120px;
        text-shadow: 0 1px 1px rgba(0,0,0,.4);
        background-color: transparent;
        border: 0;
        z-index: 10;
    }

    .carousel .item {
        max-width: 100%;
        max-height: 500px;
    }
    .carousel img {
        min-width: 866px;
        min-height: 300px;
        max-width: 100%;
        max-height: 500px;
        margin: 0 auto;
    }

    .carousel-caption {
        background-color: rgba(0, 0, 0, 0.5);
        border-radius: 20px;
        box-shadow: 2px 2px 2px #000;
        position: static;
        margin: 50px;

    }
    .carousel-caption h1,
    .carousel-caption .lead {
        margin: 0;
        line-height: 1.25;
        color: #fff;
        text-shadow: 0 1px 1px rgba(0,0,0,.4);
    }

    .carousel-caption .btn {
        margin-top: 10px;
    }

    .carousel-caption .badge {
        font-size: 15px;
        line-height: 18px;
        box-shadow: 0px 0px 10px #fff;
    }
    /* RESPONSIVE CSS
    -------------------------------------------------- */

/*
    @media (max-width: 979px) {

