        }
    }

*/
    @media (max-width: 979px) {

        .carousel {
            margin-left: -20px;
            margin-right: -20px;
        }
        .carousel .ccc {

        }
        .carousel .item {
            max-width: 700px;
            height: 300px;
        }
        .carousel img {
            height: 300px;
        }
        .carousel-caption {
            width: 65%;
            padding: 0 70px;
            margin-top: 100px;
        }
        .carousel-caption h1 {
            font-size: 30px;
        }
        .carousel-caption .lead,
        .carousel-caption .btn {
            font-size: 18px;
        }


    }





